 127	 $output->writeln('<comment>PHP Extensions:</comment>');
 128	
 129	 $extensions = [
 130	 'swoole' => 'Swoole extension for high-performance networking',
 131	 'curl' => 'cURL extension for HTTP requests',
 132	 'json' => 'JSON extension for data serialization',
 133	 'mbstring' => 'Multibyte string extension',
 134	 'openssl' => 'OpenSSL extension for encryption',
 135	 ];
 136	
 137	 foreach ($extensions as $name => $description) {
 138	 $loaded = extension_loaded($name);
 139	 $version = $loaded ? phpversion($name) : 'N/A';
 140	 $status = $loaded ? '<info>Loaded</info>' : '<error>Not Loaded</error>';
 141	 $versionStr = $version ?: 'Unknown';
 142	
 143	 $output->writeln(" {$name}: {$status} (v{$versionStr})");
 144	 }
 145	 }
 146	}
