 12	/**
 13	 * 运行时信息命令
 14	 * 显示运行时环境信息
 15	 */
 16	class RuntimeInfoCommand extends Command
 17	{
 18	 /**
 19	 * 配置命令
 20	 *
 21	 * @return void
 22	 */
 23	 protected function configure(): void
 24	 {
 25	 $this->setName('runtime:info')
 26	 ->setDescription('Show runtime information');
 27	 }
 28	
 29	 /**
 30	 * 执行命令
 31	 *
 32	 * @param Input $input 输入对象
 33	 * @param Output $output 输出对象
 34	 * @return int
 35	 */
 36	 protected function execute(Input $input, Output $output): int
 37	 {
 38	 /** @var RuntimeManager $manager */
 39	 $manager = $this->app->make('runtime.manager');
 40	
 41	 try {
 42	 $this->displaySystemInfo($output);
 43	 $this->displayRuntimeInfo($output, $manager);
 44	 $this->displayAvailableRuntimes($output, $manager);
 45	
 46	 return 0;
 47	
 48	 } catch (\Throwable $e) {
 49	 $output->writeln("<error>Failed to get runtime info: {$e->getMessage()}</error>");
