 22	
 23	 /**
 24	 * 启动适配器
 25	 *
 26	 * @return void
 27	 */
 28	 public function boot(): void;
 29	
 30	 /**
 31	 * 运行适配器
 32	 *
 33	 * @return void
 34	 */
 35	 public function run(): void;
 36	
 37	 /**
 38	 * 停止适配器
 39	 *
 40	 * @return void
 41	 */
 42	 public function terminate(): void;
 43	
 44	 /**
 45	 * 获取适配器名称
 46	 *
 47	 * @return string
 48	 */
 49	 public function getName(): string;
 50	
 51	 /**
 52	 * 检查适配器是否支持当前环境
 53	 *
 54	 * @return bool
 55	 */
 56	 public function isSupported(): bool;
 57	
 58	 /**
 59	 * 获取适配器优先级
 60	 * 数值越大优先级越高
 61	 *
 62	 * @return int
 63	 */
 64	 public function getPriority(): int;
 65	}
