Path: src/runtime/AbstractRuntime.php
 1	<?php
 2	
 3	declare(strict_types=1);
 4	
 5	namespace yangweijie\thinkRuntime\runtime;
 6	
 7	use think\App;
 8	use think\Request;
 9	use think\Response;
 10	use Psr\Http\Message\ServerRequestInterface;
 11	use Psr\Http\Message\ResponseInterface;
 12	use Nyholm\Psr7\Factory\Psr17Factory;
 13	use Nyholm\Psr7\Response as Psr7Response;
 14	use yangweijie\thinkRuntime\contract\RuntimeInterface;
 15	
 16	/**
 17	 * 抽象运行时基类
 18	 * 提供运行时的通用实现
 19	 */
 20	abstract class AbstractRuntime implements RuntimeInterface
 21	{
 22	 /**
 23	 * ThinkPHP应用实例
 24	 *
 25	 * @var App|object
 26	 */
 27	 protected $app;
 28	
 29	 /**
 30	 * 运行时配置
 31	 *
 32	 * @var array
 33	 */
 34	 protected array $config = [];
 35	
 36	 /**
 37	 * PSR-17工厂
 38	 *
 39	 * @var Psr17Factory
 40	 */
 41	 protected Psr17Factory $psr17Factory;
 42	
 43	 /**
 44	 * 构造函数
 45	 *
 46	 * @param App|object $app ThinkPHP应用实例
 47	 * @param array $config 配置数组
 48	 */
 49	 public function __construct($app, array $config = [])
 50	 {
 51	 $this->app = $app;
 52	 $this->config = $config;
 53	 $this->psr17Factory = new Psr17Factory();
 54	 }
 55	
 56	 /**
 57	 * 处理HTTP请求
 58	 *
 59	 * @param ServerRequestInterface $request PSR-7请求对象
 60	 * @return ResponseInterface PSR-7响应对象
 61	 */
 62	 public function handleRequest(ServerRequestInterface $request): ResponseInterface
 63	 {
 64	 try {
 65	 // 将PSR-7请求转换为ThinkPHP请求
 66	 $thinkRequest = $this->convertPsr7ToThinkRequest($request);
 67	
 68	 // 处理请求
 69	 $thinkResponse = $this->app->http->run($thinkRequest);
 70	
 71	 // 将ThinkPHP响应转换为PSR-7响应
 72	 return $this->convertThinkResponseToPsr7($thinkResponse);
 73	
 74	 } catch (\Throwable $e) {
 75	 // 错误处理
 76	 return $this->handleError($e);
 77	 }
 78	 }
 79	
 80	 /**
 81	 * 获取运行时配置
 82	 *
 83	 * @return array
 84	 */
 85	 public function getConfig(): array
 86	 {
 87	 return $this->config;
 88	 }
 89	
 90	 /**
 91	 * 设置运行时配置
 92	 *
 93	 * @param array $config 配置数组
 94	 * @return void
 95	 */
 96	 public function setConfig(array $config): void
 97	 {
 98	 $this->config = array_merge($this->config, $config);
 99	 }
 100	
 101	 /**
 102	 * 启动运行时
 103	 * 子类必须实现此方法
 104	 *
 105	 * @param array $options 启动选项
 106	 * @return void
 107	 */
 108	 abstract public function start(array $options = []): void;
 109	
 110	 /**
 111	 * 停止运行时
 112	 * 子类必须实现此方法
 113	 *
 114	 * @return void
 115	 */
 116	 abstract public function stop(): void;
 117	
 118	 /**
 119	 * 获取运行时名称
 120	 * 子类必须实现此方法
 121	 *
 122	 * @return string
 123	 */
 124	 abstract public function getName(): string;
 125	
 126	 /**
 127	 * 检查运行时是否可用
 128	 * 子类必须实现此方法
 129	 *
 130	 * @return bool
 131	 */
 132	 abstract public function isAvailable(): bool;
...
 218	
 219	 /**
 220	 * 处理错误
 221	 *
 222	 * @param \Throwable $e 异常
 223	 * @return ResponseInterface PSR-7响应
 224	 */
 225	 protected function handleError(\Throwable $e): ResponseInterface
 226	 {
 227	 $content = json_encode([
 228	 'error' => true,
 229	 'message' => $e->getMessage(),
 230	 'code' => $e->getCode(),
 231	 'file' => $e->getFile(),
 232	 'line' => $e->getLine(),
 233	 ], JSON_UNESCAPED_UNICODE);
 234	
 235	 return new Psr7Response(500, ['Content-Type' => 'application/json'], $content);
 236	 }
 237	}
