 329	 /**
 330	 * 处理请求核心逻辑
 331	 *
 332	 * @param mixed $request Ripple请求对象
 333	 * @param mixed $response Ripple响应对象
 334	 * @return void
 335	 */
 336	 protected function processRequest($request, $response): void
 337	 {
 338	 // 转换为PSR-7请求
 339	 $psr7Request = $this->convertRippleRequestToPsr7($request);
 340	 
 341	 // 处理请求
 342	 $psr7Response = $this->handleRequest($psr7Request);
 343	 
 344	 // 发送响应
 345	 $this->sendRippleResponse($psr7Response, $response);
 346	 }
