Path: src/runtime/AbstractRuntime.php
...
 55	
 56	 /**
 57	 * 处理HTTP请求
 58	 *
 59	 * @param ServerRequestInterface $request PSR-7请求对象
 60	 * @return ResponseInterface PSR-7响应对象
 61	 */
 62	 public function handleRequest(ServerRequestInterface $request): ResponseInterface
 63	 {
 64	 try {
 65	 // 在常驻内存运行时中，需要在每次请求前重置全局状态
 66	 $this->resetGlobalState();
 67	
 68	 // 将PSR-7请求转换为ThinkPHP请求
 69	 $thinkRequest = $this->convertPsr7ToThinkRequest($request);
 70	
 71	 // 处理请求
 72	 $thinkResponse = $this->app->http->run($thinkRequest);
 73	
 74	 // 将ThinkPHP响应转换为PSR-7响应
 75	 return $this->convertThinkResponseToPsr7($thinkResponse);
 76	
 77	 } catch (\Throwable $e) {
 78	 // 错误处理
 79	 return $this->handleError($e);
 80	 }
 81	 }
 82	
 83	 /**
 84	 * 获取运行时配置
 85	 *
 86	 * @return array
 87	 */
 88	 public function getConfig(): array
 89	 {
 90	 return $this->config;
 91	 }
...
 275	
 276	 // 清理所有HTTP_*头信息和请求相关变量
 277	 foreach ($_SERVER as $key => $value) {
 278	 if (strpos($key, 'HTTP_') === 0 ||
 279	 in_array($key, ['REQUEST_METHOD', 'REQUEST_URI', 'QUERY_STRING', 'CONTENT_TYPE', 'CONTENT_LENGTH'])) {
 280	 unset($_SERVER[$key]);
 281	 }
 282	 }
 283	
 284	 // 恢复保留的变量
 285	 $_SERVER = array_merge($_SERVER, $preserved);
 286	
 287	 // 重置 ThinkPHP 相关的静态状态（如果需要）
 288	 if (class_exists('\think\facade\App')) {
 289	 // 重置应用实例的请求状态
 290	 $this->app->request = null;
 291	 }
 292	
 293	 // 重置调试相关的全局状态
 294	 if (function_exists('debug_reset')) {
 295	 debug_reset();
 296	 }
 297	
 298	 // 重置内存使用统计
 299	 if (function_exists('memory_reset_peak_usage')) {
 300	 memory_reset_peak_usage();
 301	 }
 302	 }
