 235	 $this->httpServer->listen($this->socketServer);
 236	
 237	 // 设置连接限制
 238	 if ($config['max_connections'] > 0) {
 239	 $this->socketServer->on('connection', function ($connection) use ($config) {
 240	 // 设置超时
 241	 if ($config['timeout'] > 0) {
 242	 $connection->setTimeout($config['timeout']);
 243	 }
 244	 });
 245	 }
 246	 }
 247	
 248	 /**
 249	 * 处理HTTP请求（ReactPHP回调）
 250	 *
 251	 * @param ServerRequestInterface $request PSR-7请求
 252	 * @return \React\Promise\PromiseInterface
 253	 */
 254	 public function handleReactRequest(ServerRequestInterface $request)
 255	 {
 256	 try {
 257	 // 处理请求
 258	 $response = $this->handleRequest($request);
 259	
 260	 // 返回ReactPHP Response
 261	 return \React\Promise\resolve(
 262	 new Response(
 263	 $response->getStatusCode(),
 264	 $response->getHeaders(),
 265	 (string) $response->getBody()
 266	 )
 267	 );
 268	
 269	 } catch (\Throwable $e) {
 270	 return \React\Promise\resolve(
 271	 $this->handleReactError($e)
 272	 );
 273	 }
 274	 }
