 1	<?php
 2	
 3	declare(strict_types=1);
 4	
 5	namespace yangweijie\thinkRuntime\runtime;
 6	
 7	use think\App;
 8	use yangweijie\thinkRuntime\contract\AdapterInterface;
 9	use yangweijie\thinkRuntime\contract\RuntimeInterface;
 10	use yangweijie\thinkRuntime\config\RuntimeConfig;
 11	use yangweijie\thinkRuntime\adapter\SwooleAdapter;
 12	use yangweijie\thinkRuntime\adapter\FrankenphpAdapter;
 13	use yangweijie\thinkRuntime\adapter\ReactphpAdapter;
 14	use yangweijie\thinkRuntime\adapter\RippleAdapter;
 15	use yangweijie\thinkRuntime\adapter\RoadrunnerAdapter;
 16	use yangweijie\thinkRuntime\adapter\FpmAdapter;
 17	
 18	/**
 19	 * 运行时管理器
 20	 * 负责管理和选择合适的运行时适配器
 21	 */
 22	class RuntimeManager
 23	{
 24	 /**
 25	 * ThinkPHP应用实例
 26	 *
 27	 * @var App
 28	 */
 29	 protected App $app;
 30	
 31	 /**
 32	 * 运行时配置
 33	 *
 34	 * @var RuntimeConfig
 35	 */
 36	 protected RuntimeConfig $config;
 37	
 38	 /**
 39	 * 已注册的适配器
 40	 *
 41	 * @var array<string, string>
 42	 */
 43	 protected array $adapters = [
 44	 'swoole' => SwooleAdapter::class,
 45	 'frankenphp' => FrankenphpAdapter::class,
 46	 'reactphp' => ReactphpAdapter::class,
 47	 'ripple' => RippleAdapter::class,
 48	 'roadrunner' => RoadrunnerAdapter::class,
 49	 'fpm' => FpmAdapter::class,
 50	 ];
 51	
 52	 /**
 53	 * 当前运行时实例
 54	 *
 55	 * @var RuntimeInterface|null
 56	 */
 57	 protected ?RuntimeInterface $currentRuntime = null;
 58	
 59	 /**
 60	 * 构造函数
 61	 *
 62	 * @param App $app ThinkPHP应用实例
 63	 * @param RuntimeConfig $config 运行时配置
 64	 */
 65	 public function __construct(App $app, RuntimeConfig $config)
 66	 {
 67	 $this->app = $app;
 68	 $this->config = $config;
 69	 }
 70	
 71	 /**
 72	 * 获取运行时实例
 73	 *
 74	 * @param string|null $name 运行时名称，为null时自动检测
 75	 * @return RuntimeInterface
 76	 */
 77	 public function getRuntime(?string $name = null): RuntimeInterface
 78	 {
 79	 if ($this->currentRuntime !== null) {
 80	 return $this->currentRuntime;
 81	 }
 82	
 83	 $runtimeName = $name ?: $this->detectRuntime();
 84	 $this->currentRuntime = $this->createRuntime($runtimeName);
 85	
 86	 return $this->currentRuntime;
 87	 }
 88	
 89	 /**
 90	 * 创建运行时实例
 91	 *
 92	 * @param string $name 运行时名称
 93	 * @return RuntimeInterface
 94	 */
 95	 public function createRuntime(string $name): RuntimeInterface
 96	 {
 97	 if (!isset($this->adapters[$name])) {
 98	 throw new \InvalidArgumentException("Unknown runtime adapter: {$name}");
 99	 }
 100	
 101	 $adapterClass = $this->adapters[$name];
 102	 $runtimeConfig = $this->config->getRuntimeConfig($name);
 103	
 104	 if (!class_exists($adapterClass)) {
 105	 throw new \RuntimeException("Runtime adapter class not found: {$adapterClass}");
 106	 }
 107	
 108	 $adapter = new $adapterClass($this->app, $runtimeConfig);
 109	
 110	 if (!$adapter instanceof AdapterInterface) {
 111	 throw new \RuntimeException("Runtime adapter must implement AdapterInterface");
 112	 }
 113	
 114	 if (!$adapter instanceof RuntimeInterface) {
 115	 throw new \RuntimeException("Runtime adapter must implement RuntimeInterface");
 116	 }
 117	
 118	 return $adapter;
 119	 }
 120	
 121	 /**
 122	 * 自动检测运行时
 123	 *
 124	 * @return string
 125	 */
 126	 public function detectRuntime(): string
 127	 {
 128	 $defaultRuntime = $this->config->getDefaultRuntime();
 129	
 130	 // 如果指定了具体的运行时，直接返回
 131	 if ($defaultRuntime !== 'auto') {
 132	 return $defaultRuntime;
 133	 }
 134	
 135	 // 按优先级检测可用的运行时
 136	 $detectOrder = $this->config->getAutoDetectOrder();
 137	
 138	 foreach ($detectOrder as $runtimeName) {
 139	 if ($this->isRuntimeAvailable($runtimeName)) {
 140	 return $runtimeName;
 141	 }
 142	 }
 143	
 144	 // 如果都不可用，返回FPM作为兜底
 145	 return 'fpm';
 146	 }
...
