 134	 /**
 135	 * 将PSR-7请求转换为ThinkPHP请求
 136	 *
 137	 * @param ServerRequestInterface $request PSR-7请求
 138	 * @return Request ThinkPHP请求
 139	 */
 140	 protected function convertPsr7ToThinkRequest(ServerRequestInterface $request): Request
 141	 {
 142	 $server = [];
 143	 $headers = [];
 144	
 145	 // 转换请求头
 146	 foreach ($request->getHeaders() as $name => $values) {
 147	 $headerName = (string) $name;
 148	 $headers[$headerName] = implode(', ', $values);
 149	 $server['HTTP_' . strtoupper(str_replace('-', '_', $headerName))] = $headers[$headerName];
 150	 }
 151	
 152	 // 设置基本服务器变量
 153	 $server['REQUEST_METHOD'] = $request->getMethod();
 154	 $server['REQUEST_URI'] = (string) $request->getUri();
 155	 $server['SERVER_PROTOCOL'] = 'HTTP/' . $request->getProtocolVersion();
 156	 $server['QUERY_STRING'] = $request->getUri()->getQuery();
 157	
 158	 // 创建ThinkPHP请求
 159	 // 注意：这里需要根据实际的ThinkPHP版本调整Request创建方式
 160	 $thinkRequest = $this->app->request;
 161	
 162	 // 如果没有现有请求，创建新的
 163	 if (!$thinkRequest) {
 164	 $thinkRequest = new Request();
 165	 }
 166	
 167	 // 设置服务器变量
 168	 foreach ($server as $key => $value) {
 169	 $_SERVER[$key] = $value;
 170	 }
 171	
 172	 // 设置请求头
 173	 foreach ($headers as $name => $value) {
 174	 $_SERVER['HTTP_' . strtoupper(str_replace('-', '_', $name))] = $value;
 175	 }
 176	
 177	 // 设置POST数据
 178	 $parsedBody = $request->getParsedBody();
 179	 if (is_array($parsedBody)) {
 180	 $_POST = $parsedBody;
 181	 }
 182	
 183	 // 设置GET数据
 184	 parse_str($request->getUri()->getQuery(), $queryParams);
 185	 $_GET = $queryParams;
 186	
 187	 // 设置请求体
 188	 $body = (string) $request->getBody();
 189	 if (!empty($body)) {
 190	 // 对于JSON请求，可能需要特殊处理
 191	 if (strpos($headers['content-type'] ?? '', 'application/json') !== false) {
 192	 $jsonData = json_decode($body, true);
 193	 if (is_array($jsonData)) {
 194	 $_POST = array_merge($_POST, $jsonData);
 195	 }
 196	 }
 197	 }
 198	
 199	 return $thinkRequest;
 200	 }
 201	
 202	 /**
 203	 * 将ThinkPHP响应转换为PSR-7响应
 204	 *
 205	 * @param Response $response ThinkPHP响应
 206	 * @return ResponseInterface PSR-7响应
 207	 */
 208	 protected function convertThinkResponseToPsr7(Response $response): ResponseInterface
 209	 {
 210	 $psr7Response = new Psr7Response(
 211	 $response->getCode(),
 212	 $response->getHeader(),
 213	 $response->getContent()
 214	 );
 215	
 216	 return $psr7Response;
 217	 }
 218	
 219	 /**
 220	 * 处理错误
 221	 *
 222	 * @param \Throwable $e 异常
 223	 * @return ResponseInterface PSR-7响应
 224	 */
 225	 protected function handleError(\Throwable $e): ResponseInterface
 226	 {
 227	 $content = json_encode([
 228	 'error' => true,
 229	 'message' => $e->getMessage(),
 230	 'code' => $e->getCode(),
 231	 'file' => $e->getFile(),
 232	 'line' => $e->getLine(),
 233	 ], JSON_UNESCAPED_UNICODE);
 234	
 235	 return new Psr7Response(500, ['Content-Type' => 'application/json'], $content);
 236	 }
 237	}
