Path: src/runtime/AbstractRuntime.php
...
 55	
 56	 /**
 57	 * 处理HTTP请求
 58	 *
 59	 * @param ServerRequestInterface $request PSR-7请求对象
 60	 * @return ResponseInterface PSR-7响应对象
 61	 */
 62	 public function handleRequest(ServerRequestInterface $request): ResponseInterface
 63	 {
 64	 try {
 65	 // 在常驻内存运行时中，需要在每次请求前重置全局状态
 66	 $this->resetGlobalState();
 67	
 68	 // 将PSR-7请求转换为ThinkPHP请求
 69	 $thinkRequest = $this->convertPsr7ToThinkRequest($request);
 70	
 71	 // 处理请求
 72	 $thinkResponse = $this->app->http->run($thinkRequest);
 73	
 74	 // 将ThinkPHP响应转换为PSR-7响应
 75	 return $this->convertThinkResponseToPsr7($thinkResponse);
 76	
 77	 } catch (\Throwable $e) {
 78	 // 错误处理
 79	 return $this->handleError($e);
 80	 }
 81	 }
 82	
 83	 /**
 84	 * 获取运行时配置
 85	 *
 86	 * @return array
 87	 */
 88	 public function getConfig(): array
 89	 {
 90	 return $this->config;
 91	 }
...
