 69	
 70	 /**
 71	 * 应用全局配置
 72	 *
 73	 * @return void
 74	 */
 75	 protected function applyGlobalConfig(): void
 76	 {
 77	 /** @var RuntimeConfig $config */
 78	 $config = $this->app->make('runtime.config');
 79	 $globalConfig = $config->getGlobalConfig();
 80	
 81	 // 应用PHP配置
 82	 foreach ($globalConfig as $key => $value) {
 83	 switch ($key) {
 84	 case 'error_reporting':
 85	 error_reporting($value);
 86	 break;
 87	 case 'display_errors':
 88	 ini_set('display_errors', $value ? '1' : '0');
 89	 break;
 90	 case 'log_errors':
 91	 ini_set('log_errors', $value ? '1' : '0');
 92	 break;
 93	 case 'memory_limit':
 94	 ini_set('memory_limit', $value);
 95	 break;
 96	 case 'max_execution_time':
 97	 set_time_limit($value);
 98	 break;
 99	 }
 100	 }
 101	 }
