 73	 /**
 74	 * 启动适配器
 75	 *
 76	 * @return void
 77	 */
 78	 public function boot(): void
 79	 {
 80	 if (!$this->isSupported()) {
 81	 throw new RuntimeException('Bref runtime is not available');
 82	 }
 83	
 84	 $config = $this->getConfig();
 85	
 86	 // 设置错误处理
 87	 if (!($config['error']['display_errors'] ?? false)) {
 88	 ini_set('display_errors', '0');
 89	 }
 90	
 91	 if ($config['error']['log_errors'] ?? true) {
 92	 ini_set('log_errors', '1');
 93	 }
 94	
 95	 // 设置内存限制
 96	 if (isset($config['lambda']['memory'])) {
 97	 ini_set('memory_limit', $config['lambda']['memory'] . 'M');
 98	 }
 99	
 100	 // 检测Lambda环境
 101	 $this->detectLambdaEnvironment();
 102	 }
