 91	 /**
 92	 * 运行适配器
 93	 *
 94	 * @return void
 95	 */
 96	 public function run(): void
 97	 {
 98	 if ($this->server === null) {
 99	 $this->boot();
 100	 }
 101	
 102	 $config = array_merge($this->defaultConfig, $this->config);
 103	 
 104	 echo "Ripple HTTP Server starting...\n";
 105	 echo "Listening on: {$config['host']}:{$config['port']}\n";
 106	 echo "Workers: {$config['worker_num']}\n";
 107	 echo "Max Coroutines: {$config['max_coroutines']}\n";
 108	 echo "Fiber Support: " . ($config['enable_fiber'] ? 'Yes' : 'No') . "\n";
 109	 echo "Coroutine Pool: {$config['coroutine_pool_size']}\n";
 110	 echo "Press Ctrl+C to stop the server\n\n";
 111	 
 112	 // 启动Ripple服务器
 113	 $this->startRippleServer();
 114	 }
 115	
 116	 /**
 117	 * 启动运行时
 118	 *
 119	 * @param array $options 启动选项
 120	 * @return void
 121	 */
 122	 public function start(array $options = []): void
 123	 {
 124	 $this->setConfig($options);
 125	 $this->run();
 126	 }
