 132	 /**
 133	 * 用户配置
 134	 *
 135	 * @var array
 136	 */
 137	 protected array $userConfig = [];
 138	
 139	 /**
 140	 * 构造函数
 141	 *
 142	 * @param array $config 用户配置
 143	 */
 144	 public function __construct(array $config = [])
 145	 {
 146	 $this->userConfig = $config;
 147	 }
 148	
 149	 /**
 150	 * 获取配置
 151	 *
 152	 * @param string|null $key 配置键，为null时返回所有配置
 153	 * @param mixed $default 默认值
 154	 * @return mixed
 155	 */
 156	 public function get(?string $key = null, $default = null)
 157	 {
 158	 $config = $this->mergeConfig($this->defaultConfig, $this->userConfig);
 159	
 160	 if ($key === null) {
 161	 return $config;
 162	 }
 163	
 164	 return $this->getNestedValue($config, $key, $default);
 165	 }
 166	
 167	 /**
 168	 * 设置配置
 169	 *
 170	 * @param string $key 配置键
 171	 * @param mixed $value 配置值
 172	 * @return void
 173	 */
 174	 public function set(string $key, $value): void
 175	 {
 176	 $this->setNestedValue($this->userConfig, $key, $value);
 177	 }
 178	
 179	 /**
 180	 * 获取运行时配置
 181	 *
 182	 * @param string $runtime 运行时名称
 183	 * @return array
 184	 */
 185	 public function getRuntimeConfig(string $runtime): array
 186	 {
 187	 return $this->get("runtimes.{$runtime}", []);
 188	 }
 189	
 190	 /**
 191	 * 获取默认运行时
 192	 *
 193	 * @return string
 194	 */
 195	 public function getDefaultRuntime(): string
 196	 {
 197	 return $this->get('default', 'auto');
 198	 }
