 73	 /**
 74	 * 启动适配器
 75	 *
 76	 * @return void
 77	 */
 78	 public function boot(): void
 79	 {
 80	 if (!$this->isSupported()) {
 81	 throw new RuntimeException('Ripple is not available');
 82	 }
 83	
