 170	 /**
 171	 * 检查适配器是否支持当前环境
 172	 *
 173	 * @return bool
 174	 */
 175	 public function isSupported(): bool
 176	 {
 177	 // 检查PHP版本是否支持Fiber
 178	 if (version_compare(PHP_VERSION, '8.1.0', '<')) {
 179	 return false;
 180	 }
 181	 
 182	 // 检查Ripple相关类是否存在
 183	 return class_exists('Ripple\\Http\\Server') || 
 184	 class_exists('Ripple\\Server\\Server') ||
 185	 function_exists('ripple_server_create');
 186	 }
