 198	 /**
 199	 * 获取运行时配置（合并默认配置）
 200	 *
 201	 * @return array
 202	 */
 203	 public function getConfig(): array
 204	 {
 205	 return array_merge($this->defaultConfig, $this->config);
 206	 }
