Path: src/adapter/SwooleAdapter.php
...
 150	
 151	 /**
 152	 * 启动适配器
 153	 *
 154	 * @return void
 155	 * @throws ErrorException
 156	 */
 157	 public function boot(): void
 158	 {
 159	 if (!$this->isSupported()) {
 160	 throw new RuntimeException('Swoole extension is not installed');
 161	 }
 162	
 163	 $config = array_merge($this->defaultConfig, $this->config);
 164	
 165	 // 设置协程 Hook
 166	 if (isset($config['settings']['hook_flags']) && class_exists('\Swoole\Runtime')) {
 167	 Runtime::enableCoroutine($config['settings']['hook_flags']);
 168	 }
 169	
 170	 // 初始化请求创建器（复用实例）
 171	 $this->requestCreator = new ServerRequestCreator(
 172	 $this->psr17Factory,
 173	 $this->psr17Factory,
 174	 $this->psr17Factory,
 175	 $this->psr17Factory
 176	 );
 177	
 178	 // 设置文档根目录
 179	 if (empty($config['settings']['document_root'])) {
 180	 $config['settings']['document_root'] = $this->getPublicPath();
 181	 }
 182	
 183	 $this->server = new Server(
 184	 $config['host'],
 185	 $config['port'],
 186	 $config['mode'],
 187	 $config['sock_type']
 188	 );
 189	
 190	 // 设置服务器配置
 191	 $this->server->set($config['settings']);
 192	
 193	 // 绑定事件
 194	 $this->bindEvents();
 195	
 196	 // 初始化中间件
 197	 $this->initMiddlewares();
 198	
 199	 // 初始化应用
 200	 $this->app->initialize();
 201	 }
 202	
 203	 /**
 204	 * 初始化中间件
 205	 *
 206	 * @return void
 207	 */
 208	 protected function initMiddlewares(): void
 209	 {
 210	 $config = array_merge($this->defaultConfig, $this->config);
 211	
 212	 // 添加 CORS 中间件
 213	 if ($config['middleware']['cors']['enable'] ?? true) {
 214	 $this->addMiddleware([$this, 'corsMiddleware']);
 215	 }
 216	
 217	 // 添加安全中间件
 218	 if ($config['middleware']['security']['enable'] ?? true) {
 219	 $this->addMiddleware([$this, 'securityMiddleware']);
 220	 }
 221	 }
...
